mapper.select:
SELECT info.album_id, info.album_title, info.cover_url, info.include_track_count, info.is_finished, info.status, max(if(info.stat_type = '0401', info.stat_num, 0)) playStatNum, max(if(info.stat_type = '0402', info.stat_num, 0)) subscribeStatNum, max(if(info.stat_type = '0403', info.stat_num, 0)) buyStatNum, max(if(info.stat_type = '0404', info.stat_num, 0)) albumCommentStatNum FROM (SELECT ast.album_id, ai.album_title, ai.cover_url, ai.include_track_count, ai.is_finished, ai.status, ast.stat_type, ast.stat_num FROM album_info ai INNER JOIN album_stat ast ON ai.id = ast.album_id
ai.user_id = #{albumInfoQuery.userId}
AND ai.status = #{albumInfoQuery.status}
AND ai.album_title = #{albumInfoQuery.albumTitle}
)info GROUP BY info.album_id ORDER BY info.album_id DESC
